(()=>{document.getElementById("submit-city").addEventListener("click",(()=>{(async e=>{await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${e}&appid=142774961802d3eabc9decc1c8e0cf4a`).then((e=>e.json())).then((e=>{console.log(e),localStorage.setItem("weatherInformation",e)})).catch((e=>console.log(e)))})(document.getElementById("city-name").value)}));const e=localStorage.getItem("weatherInformation");console.log("From localStorage",e)})();